import React, { useState } from "react";
import { TextField, Button, Container, Typography } from "@mui/material";
import axios from "axios";

function Register() {
  const [username, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setrole] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
        const response = await axios.post("http://localhost:5000/api/auth/register", { 
            username, // ‚úÖ Fix: Send "username" instead of "name"
            email, 
            password 
        }, {
            headers: { "Content-Type": "application/json" } // üü¢ Ensure JSON
        });
        
        console.log("‚úÖ Registration Successful:", response.data);
        alert("Registration Successful");
    } catch (error) {
        console.error("‚ùå Registration Failed:", error.response?.data);
        alert(error.response?.data?.message || "Registration Failed");
    }
};




  return (
    <Container>
      <Typography variant="h4">Register</Typography>
      <form onSubmit={handleSubmit}>
        <TextField fullWidth label="Name" margin="normal" value={username} onChange={(e) => setName(e.target.value)} required />
        <TextField fullWidth label="Email" margin="normal" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <TextField fullWidth label="Password" type="password" margin="normal" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <Button type="submit" variant="contained" color="primary" fullWidth>
          Register
        </Button>
      </form>
    </Container>
  );
}

export default Register;
