import React from "react";
import { AppBar, Toolbar, Button } from "@mui/material";
import { Link } from "react-router-dom";

function Navbar() {
  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user"));

  return (
    <AppBar position="static">
      <Toolbar>
        <Button color="inherit" component={Link} to="/">Home</Button>

        {/* ✅ Show Dashboard only if logged in */}
        {token && <Button color="inherit" component={Link} to="/dashboard">Dashboard</Button>}

        {/* ✅ Show Admin Page only for admins */}
        {token && user?.role === "admin" && <Button color="inherit" component={Link} to="/admin">Admin</Button>}

        {!token ? (
          <>
            <Button color="inherit" component={Link} to="/login">Login</Button>
            <Button color="inherit" component={Link} to="/register">Register</Button>
          </>
        ) : (
          <Button color="inherit" onClick={() => {
            localStorage.removeItem("token");
            localStorage.removeItem("user");
            window.location.href = "/"; // ✅ Refresh the page after logout
          }}>Logout</Button>
        )}
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
