import { useState } from "react";
import axios from "axios";

const Register = () => {
  const [form, setForm] = useState({ username: "", email: "", password: "" });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/auth/register", form);
      alert("User registered successfully! Please log in.");
    } catch (err) {
      alert(err.response?.data?.message || "Registration failed.");
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen">
      <h2 className="text-2xl mb-4">Register</h2>
      <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
        <input type="text" name="username" placeholder="Username" onChange={handleChange} required className="border p-2" />
        <input type="email" name="email" placeholder="Email" onChange={handleChange} required className="border p-2" />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} required className="border p-2" />
        <button type="submit" className="bg-green-500 px-4 py-2 text-white">
          Register
        </button>
      </form>
    </div>
  );
};

export default Register;
